import axios from 'axios';

const API_URL = "http://localhost:8080/user";

class UserService {
  register(user) {
    return axios.post(`${API_URL}/crearUsuario`, user);
  }

  login(username, password) {
    // Aquí se asume que el backend usa el endpoint /login para autenticación.
    // Modifica esto según la implementación de tu backend.
    return axios.post(`${API_URL}/login`, { username, password });
  }

  getUserById(id) {
    return axios.get(`${API_URL}/obtenerUsuarioPorId/${id}`);
  }
}

export default new UserService();
